mapper.select:
select

from collect_plate_money_ranking
where 1 = 1

  and type = #{type,jdbcType=VARCHAR}


  and date_type = #{dateType,jdbcType=VARCHAR}


  and code = #{code,jdbcType=VARCHAR}


  and name = #{name,jdbcType=VARCHAR}


  and zdf = #{zdf,jdbcType=VARCHAR}


  and main_net_inflow = #{mainNetInflow,jdbcType=VARCHAR}


  and main_net_proportion = #{mainNetProportion,jdbcType=VARCHAR}


  and very_large_single_net = #{veryLargeSingleNet,jdbcType=VARCHAR}


  and very_large_single_proportion = #{veryLargeSingleProportion,jdbcType=VARCHAR}


  and large_single_net = #{largeSingleNet,jdbcType=VARCHAR}


  and large_single_proportion = #{largeSingleProportion,jdbcType=VARCHAR}


  and single_net = #{singleNet,jdbcType=VARCHAR}


  and single_net_proportion = #{singleNetProportion,jdbcType=VARCHAR}


  and small_single_net = #{smallSingleNet,jdbcType=VARCHAR}


  and small_single_proportion = #{smallSingleProportion,jdbcType=VARCHAR}


  and inflow_stock_code = #{inflowStockCode,jdbcType=VARCHAR}


  and inflow_stock_name = #{inflowStockName,jdbcType=VARCHAR}


  and create_time = #{createTime,jdbcType=TIMESTAMP}


  and date_format(create_time,'%Y-%m-%d') = #{createTimeStr,jdbcType=TIMESTAMP}


  LIMIT #{offset,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}
